%tmp_z = tmp_z + k_val^2 * conj(tmp_a)*tmp_wignerd*tmp_b*(i.^tmp_l2)*4*pi*tmp_j1*tmp_sy*conj(tmp_Ylm_d)*tmp_Ylm_s;
