%tmp_ij_ = intersectall({find(svd3_l2_==tmp_l2),find(svd3_m2_==tmp_m2),find(svd3_nx_==tmp_nx)});
tmp_ij_ = tmp_ij__{nsvd3_l2m2nx};
if (verbose>1); disp(sprintf(' %% nsvd3_l2m2nx %d/%d, %d sub-terms found',nsvd3_l2m2nx,n_svd3_l2m2nx,length(tmp_ij_))); end;
for nij=1:length(tmp_ij_);
assert(tmp_nx==svd3_nx_(tmp_ij_(nij)));
assert(tmp_l2==svd3_l2_(tmp_ij_(nij)));
assert(tmp_m2==svd3_m2_(tmp_ij_(nij)));
tmp_l1 = svd3_l1_(tmp_ij_(nij));
tmp_m1 = svd3_m1_(tmp_ij_(nij));
