tmp_b = 0; if (l_b4<=n_l & abs(m_b4)<=l_b4); tmp_b = b_(ix + 1+l_b4*(l_b4+1)+m_b4); end;
tmp_wignerd = 0; if (l_b1<=n_l & abs(m_b4)<=l_b1 & abs(m_b1)<=l_b1); tmp_wignerd = W_{1+l_b1}(1 + l_b1 + m_b1,1 + l_b4 + m_b4); end;%if (abs(m_b4)<=l_b1 & abs(m_b1)<=l_b1);
if (tmp_a~=0 & tmp_b~=0 & tmp_wignerd~=0);
tmp_z = tmp_z + k_val^2 * conj(tmp_a)*tmp_wignerd*tmp_b*(i.^tmp_l2)*4*pi*tmp_sy*tmp_ss*V_r__(1+nk,tmp_nx);
